import CardList from './CardList' ;
import {robots} from './Robots';
import SearchBox from './SearchBox';

class App extends React.Component{

    constructor(){
        super()
        this.state ={
            robots: robots,
            searchfield:''

        }
    }

  onSearchEvent(event){
      console.log(event.target.value);
  }

    render(){
        return(
            <div className='tc'>
                <h1>RoboFriends</h1>
                <SearchBox searchChange={this.state.onSearchEvent} />
    <CardList robots={this.state.robots}/>
    </div>
        );
    }

}

export default App;
